<template>
  <ion-page>
    <ion-header :translucent="true">
      <ion-toolbar>
        <ion-title>Blank</ion-title>
      </ion-toolbar>
    </ion-header>
    <ion-content :fullscreen="true">
      <ion-header collapse="condense">
        <ion-toolbar>
          <ion-title size="large">Parking</ion-title>
        </ion-toolbar>
      </ion-header>

      <ion-list>
        <ion-item>
            <div>
            <ion-img src="data:image/jpeg;base64,"></ion-img>
            </div>
          <div>
            <ion-label>{{ parking.name }}</ion-label>
            <ion-label>Place disponible: {{ parking.capacite }} </ion-label>
            </div>
            <div>
              
            <ion-button :router-link="`/connexion/${parking.id}`" color="primary">Connectez vous pour reserver</ion-button>
              
          </div>
        </ion-item>
      </ion-list>
    </ion-content>
  </ion-page>
</template>

<script>
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonList,
  IonItem,
  IonImg,
  IonLabel,
  IonButton
} from "@ionic/vue";

export default {
  components: {
    IonContent,
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonList,
    IonItem,
    IonImg,
    IonLabel,
    IonButton
  },
  data() {
        return {
            cityId: this.$route.params.id
        }
    },
    computed: {
        parking() {
            return this.$store.getters.park(this.cityId);
        }
    },
    watch: {
        '$route'(currentRoute) {
            this.cityId = currentRoute.params.id
        }
    }
};
</script>

